inp = ''

vals = {'A': 0, 'x': 0, 'B': 1, 'C': 3, 'D': 5}
totalPotions = 0

for i in range(0, len(inp), 3):
    fir, sec, thr = inp[i], inp[i+1], inp[i+2]
    group = fir + sec + thr

    currTotal = vals[fir] + vals[sec] + vals[thr]

    if group.count('x') == 1:
        currTotal += 2
    elif group.count('x') == 0:
        currTotal += 6
    
    totalPotions += currTotal

print(totalPotions)